mapper.update:
UPDATE	cart
SET		cart_amount = #{cartAmount}
WHERE	cart_id = #{cartId} AND u_id = #{uId}

UPDATE	cart
SET		cart_amount = #{cartAmount}
WHERE	u_id = #{uId} AND p_id = #{pId}

mapper.select:
SELECT	COUNT(*)
FROM	cart
WHERE	u_id = #{uId}

SELECT	cart_id, cart_amount, u_id, p_id
FROM	cart
WHERE	u_id = #{uId} AND p_id = #{pId}
